
document.addEventListener('DOMContentLoaded', function () {
    const modal = document.getElementById('myModal');
    const btn = document.getElementById('modalBtn');
    const images = [
        '../images/haeundae1.jpg',
        '../images/haeundae2.jpg',
        '../images/haeundae3.jpg',
        '../images/haeundae4.jpg'
    ];

    // Function to initialize the slider
    function initSlider() {
        $('#imageSlider').slick({
            dots: true,
            infinite: true,
            speed: 1000,
            slidesToShow: 3,
            slidesToScroll: 1
        });
    }

    // Populate the slider with images
    function populateSlider() {
        const slider = document.getElementById('imageSlider');
        images.forEach(image => {
            const imgDiv = document.createElement('div');
            imgDiv.innerHTML = `<img src="${image}" class="slider-items">`;
            slider.appendChild(imgDiv);
        });
    }

    // Open modal
    btn.onclick = function() {
        modal.style.display = "block";
        populateSlider();
        initSlider();
    }

    // Close modal on outside click
    window.onclick = function(event) {
        if (event.target == modal) {
            modal.style.display = "none";
            // Optionally reset the slider
        }
    }
});
